import React, { useEffect, useState } from 'react'
import { account } from '../appwrite/appwriteConsig'
import { databases } from '../appwrite/appwriteConsig'

const Message = ({message, name}) => {
    const [userData, setUserData] = useState()

    useEffect(() => {
        const getUser = account.get()

        getUser.then(res => {
            setUserData(res)
        }, err => {
            console.log(err)
        })

    }, [])



  return (
    <div>
        {userData && (
                <div className={userData.name === name ? "text-red-600 flex  border border-red-600 w-40  gap-2" : "flex gap-2"}>
                    <p>{name}: </p>
                    <p>{message}</p>
                </div>
        )}
    </div>
  )
}

export default Message
