import React, { useEffect, useState } from 'react'
import { account } from '../appwrite/appwriteConsig'

const Message = ({message, name}) => {
    // const [messages, setMessages] = useState([])

    const [userData, setUserData] = useState()

    useEffect(() => {
        const getUser = account.get()

        getUser.then(res => {
            setUserData(res)
        }, err => {
            console.log(err)
        })

    }, [])

    // useEffect(() => {
    //     databases.listDocuments('62e2c1b392b46ec6a069').then(res => {
    //         setMessages(res.documents)
    //     }, err => {
    //         console.log(err)
    //     })
    // }, [messages])

  return (


    <div>
        {userData && <div
                        className={userData.name === name ? "text-white rounded-xl px-4 gap-2 mt-2 mb-2 font-bold bg-blue-600 ml-[70%] mr-4 border rounded-br-xl py-4" : "flex text-white rounded-xl px-4 gap-2 mt-2 mb-2 font-bold bg-gray-100 mr-4 border rounded-br-xl py-4"}>
                            <p>{userData.name === name ? '' : `${name}:`}</p>
                            <p className='break-words'>{message}</p>
                    </div>}
    </div>
  )
}

export default Message
