import React, { useEffect, useState } from 'react'
import { account } from '../appwrite/appwriteConsig'
import { databases } from '../appwrite/appwriteConsig'

const Message = () => {
    const [messages, setMessages] = useState([])

    const [userData, setUserData] = useState()

    useEffect(() => {
        const getUser = account.get()

        getUser.then(res => {
            setUserData(res)
        }, err => {
            console.log(err)
        })

    }, [])

    useEffect(() => {
        databases.listDocuments('62e26f734e1430b62fec').then(res => {
            setMessages(res.documents)
        }, err => {
            console.log(err)
        })
    }, [messages])

    console.log(messages)


  return (


    <div>
        {userData && messages.map((message) => {
            return <div key={message.$id} 
                        className={userData.name === message.name ? "text-red-600 flex justify-end px-4 gap-2 mt-2 mb-2" : "flex gap-2 mt-2 mb-2"}>
                            <p>{userData.name === message.name ? '' : `${message.name}:`}</p>
                            <p>{message.message}</p>
                    </div>
        })}
    </div>
  )
}

export default Message
